import { useState } from 'react'

import ExpenseForm from './ExpenseForm';
import './NewExpense.css';

const FORM_INIT = <button className="new-expense-button" onClick={showFormHandler}>Add New Expense</button>

function NewExpense(props) {
    const [form, setForm] = useState(FORM_INIT);

    function formDataHandler(expenseData) {
        const expense = {
            ...expenseData,
            id: Math.random().toString()
        };
        props.onProperExpenseData(expense);
    }

    function showFormHandler() {
        setForm(<ExpenseForm onExpenseFormSubmit={formDataHandler} />)
    }

    return (
        <div className="new-expense">
            {form}
        </div>
    );
}

export default NewExpense;
