import ExpenseForm from './ExpenseForm';
import './NewExpense.css';

function NewExpense(props) {
    let formContent = <button className="new-expense-button" onClick={showFormHandler}>Add New Expense</button>

    function formDataHandler(expenseData) {
        const expense = {
            ...expenseData,
            id: Math.random().toString()
        };
        props.onProperExpenseData(expense);
    }

    function showFormHandler() {
        formContent = <ExpenseForm onExpenseFormSubmit={formDataHandler} />
    }

    return (
        <div className="new-expense">
            <button className="new-expense-button" onClick={showFormHandler}>Add New Expense</button>
        </div>
    );
}

export default NewExpense;
